 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 100, 'bike 1', 50, 1020.0, 1, 5, 200.0, true, 1, 1, 'A bicicleta ideal para os entusiastas de aventuras ao ar livre e ciclistas urbanos. Com um design robusto e leve, a Bike 1 combina estilo e funcionalidade. Equipada com um quadro de alta resistência, rodas duráveis e um sistema de transmissão suave, proporciona uma experiência de pedalada excepcional. Perfeita para explorar trilhas ou navegar pela cidade com conforto e confiança.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 101, 'bike 2', 30, 3040.0, 1, 5, 200.0, true, 1, 1, 'A Bike 2 é uma bicicleta premium projetada para entusiastas que buscam desempenho e estilo. Construída com materiais de alta qualidade, esta bicicleta oferece uma condução estável e ágil em qualquer terreno. Equipada com componentes avançados, como freios de alta performance e um sistema de suspensão eficaz, é a escolha ideal para ciclistas que valorizam qualidade e desempenho em cada pedalada.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 102, 'Ferramenta 1', 20, 300.0, 1, 5, 90.0, true, 1, 1, 'A Ferramenta 1 é indispensável para qualquer kit de ferramentas. Com um design ergonômico e durável, esta ferramenta é projetada para lidar com uma variedade de tarefas. Ideal para reparos domésticos ou trabalhos profissionais, oferece precisão e eficiência em cada utilização. Sua construção robusta garante confiabilidade e durabilidade, tornando-a uma escolha confiável para qualquer projeto.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 103, 'Pneu 1', 20, 2000.0, 1, 5, 90.0, true, 1, 1, 'O Pneu 1 é projetado para oferecer desempenho superior e durabilidade em todos os tipos de terreno. Com uma estrutura resistente e um padrão de banda de rodagem otimizado, proporciona aderência excepcional e controle preciso. Ideal para ciclistas que buscam confiança em condições variadas, este pneu combina resistência com um design moderno e eficiente.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 104, 'bike 5', 20, 2200.0, 1, 5, 500.0, true, 1, 1, 'A Bike 5 é uma bicicleta premium que redefine o conforto e o desempenho. Com um quadro leve e aerodinâmico, oferece uma experiência de condução suave e estável. Equipada com componentes de alta qualidade, como freios de precisão e um sistema de transmissão eficiente, é ideal tanto para deslocamentos diários quanto para aventuras mais longas. Uma escolha perfeita para ciclistas que valorizam qualidade e estilo em cada passeio.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 105, 'Ferramenta 2A', 20, 2700.0, 1, 5, 1200.0, true, 1, 1, 'A Ferramenta 2A é uma ferramenta multifuncional projetada para máxima versatilidade e durabilidade. Com um design compacto e ergonômico, oferece desempenho excepcional em uma variedade de aplicações. Ideal para trabalhos de manutenção e reparo, esta ferramenta robusta proporciona precisão e eficiência em cada uso. Perfeita para profissionais e entusiastas que exigem o melhor em suas ferramentas.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 106, 'Pneu Aro18', 20, 299.0, 1, 5, 99.0, true, 1, 1, 'O Pneu Aro18 é a escolha ideal para ciclistas que buscam desempenho e confiabilidade. Com um design leve e resistente, oferece uma aderência excepcional em terrenos variados. Equipado com uma estrutura durável e um padrão de banda de rodagem otimizado, proporciona controle preciso e conforto durante a pedalada. Ideal para ciclistas urbanos e off-road que valorizam qualidade e segurança em seus pneus.', 'nome longooooo');
 insert into produto (ativo, id,nome, estoque, preco, cor, estrelas, valorcompra, visivel, fornecedor, marca, descricao, nomelongo) values(true, 107, 'Pneu 29x2.2', 20, 440.0, 1, 5, 190.0, true, 1, 1, 'O Pneu 29x2.2 é projetado para oferecer desempenho superior em mountain bikes. Com um design robusto e uma construção durável, proporciona tração excepcional e resistência ao desgaste. Equipado com um padrão de banda de rodagem agressivo, oferece aderência máxima em terrenos difíceis. Ideal para ciclistas que buscam aventuras off-road com confiança e segurança.', 'nome longooooo');
